public class Dowhile_even {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		int i=1;
		do
		{
			if(i%2==0)
			{
				System.out.println(i+" is an EVEN Number");
				
			}
			i++;
				}while(i<=20);
	
	}

}
