public class Modulus {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=20,c=30,modulus=0;
		modulus=a%b%c;
		System.out.println("The Modulus of the Numbers are "+modulus); //Modulus of the numbers

	}

}
