public class Odd_sum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int a=11,b=20,c=33,odd_sum=0;
	if(a%2!=0)
		odd_sum+=a;
	if(b%2!=0)
		odd_sum+=b;
	if(c%2!=0)
		odd_sum+=c;
	System.out.println("The sum of the ODD Numbers are "+odd_sum);
	}

}
