public class Subtraction {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10,b=20,sub=0;
		if(a<b)			//finding the smallest
		{
			sub=b-a;
			
		}
		else
			{
			sub=a-b;
			}
		System.out.println("The Value is "+sub);
	}

}
